mapper.sql:
where 1=1 and deleted = 0


		and m.id = #{id}


	 	and m.name like '%'+#{name}+'%'


	    and m.url = #{url}


	    and m.parentId = #{parentId}


	    and m.parentId is null


	    and m.createTime = #{createTime}


	    and m.rank = #{rank}


	    and m.actions = #{actions}


	    and m.sysid = #{sysid}
