mapper.sql:
where 1=1


		and id = #{id}


	    and greenhouseid = #{greenhouseid}


	    and baseid = #{baseid}


	    and variety = #{variety}


	    and plantArea = #{plantArea}


	    and plantationMember = #{plantationMember}


	    and technician = #{technician}


	    and wages = #{wages}


	    and startTime = #{startTime}


	    and startTime >= #{startStartTime}


	    and startTime <= #{endStartTime}


	    and endTime = #{endTime}


	    and remark = #{remark}
